import { useEffect, useState } from "react";
import axios from "axios";
function AddScheme({ ReRender }) {
  const [name, setName] = useState();
  const [launchedOn, setLaunchedOn] = useState();
  const [applyBy, setApplyBy] = useState();
  const [beneficiaries, setBeneficiaries] = useState();
  const [status, setStatus] = useState();

  useEffect(() => {
    let form = document.getElementById;
  }, []);

  function formHandle(e) {
    e.preventDefault();
    const data = {
      name: name,
      launchedOn: launchedOn,
      applyBy: applyBy,
      beneficiaries: beneficiaries,
      status: status,
    };
    console.log(data);
    axios
      .post("http://localhost:3002/schemes", data)
      .then((result) => {
        if (result.data == true) {
          ReRender();
          console.log("scheme added");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }

  return (
    <div>
      <form
        id="form"
        action=""
        method="post"
        onSubmit={(e) => {
          formHandle(e);
        }}
      >
        <label htmlFor="name">Scheme name</label>
        <input
          required="True"
          type="text"
          name="name"
          id=""
          onChange={(e) => {
            setName(e.target.value);
          }}
        />
        <br />
        <label htmlFor="launchedOn">Launched On</label>
        <input
          required="True"
          type="date"
          name="launchedOn"
          id=""
          onChange={(e) => {
            setLaunchedOn(e.target.value);
          }}
        />
        <br />
        <label htmlFor="applyBy">Apply By</label>
        <input
          required="True"
          type="date"
          name="applyBy"
          id=""
          onChange={(e) => {
            setApplyBy(e.target.value);
          }}
        />
        <br />
        <label htmlFor="beneficiaries">Beneficiaries</label>
        <input
          required="True"
          type="text"
          name="beneficiaries"
          id=""
          onChange={(e) => {
            setBeneficiaries(e.target.value);
          }}
        />
        <br />
        <label htmlFor="status">Status</label>
        <input
          required="True"
          type="text"
          name="status"
          id=""
          onChange={(e) => {
            setStatus(e.target.value);
          }}
        />
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default AddScheme;
